// limitations in javascript don't allow for this solution to pass all test cases
function acmTeam(topics) {
  let n = topics.length;
  let biTopics = [];
  for (let i=0; i<n; i++)
    biTopics[i] = parseInt(topics[i], 2);
  console.log(biTopics);
  let maxTopic = 0;
  let teamCount = 0;
  for (let i=0; i<n-1; i++) {
    for (let j=i+1; j<n; j++) {
      let tempo = biTopics[i] | biTopics[j];
      console.log(tempo);
      console.log(tempo.toString(2));
      let bitCount = (tempo.toString(2).match(/1/g) || []).length;
      if (bitCount > maxTopic) {
        maxTopic = bitCount;
        teamCount = 1;
      } else if (bitCount == maxTopic) {
        teamCount++;
      }
    }
  }
  return [maxTopic, teamCount];
}

