function acmTeam(topics) {
  let maxTopics = 0;
  let numTeams = 0;
  for (let i=0; i<topics.length-1; i++) {
    for (let j=i+1; j<topics.length; j++) {
      let topicsCount = 0;
      // We find the number of topics known by the pair
      for (let k=0; k<topics[0].length; k++) {
        if (topics[i][k] === '1' || topics[j][k] === '1') {
          topicsCount++;
        }
      }
      // Compare with the stored value of the max num of topics per pair
      if (topicsCount > maxTopics)
      {
        maxTopics = topicsCount;
        numTeams = 1;
      }
      // We find another instance of the same max num of topics in a pair
      else if (maxTopics == topicsCount) {
        numTeams++;
      }
    }
  }
  return [maxTopics, numTeams];
}

